// src/FileUpload.js
import React, { useState } from 'react';
//import './FileUpload.css';
import axios from 'axios';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [uploadedFileName, setUploadedFileName] = useState('');
  const [summary, setSummary] = useState('');

  const onFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const onFileUpload = async () => {
    const formData = new FormData();
    formData.append('document', file);

    try {
      const uploadRes = await axios.post('http://localhost:3000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setUploadedFileName(uploadRes.data.filename);

      const summarizeRes = await axios.post('http://localhost:3000/summarize', {
        filename: uploadRes.data.filename,
      });

      setSummary(summarizeRes.data.summary);
    } catch (err) {
      console.error('Error uploading file:', err);
    }
  };

  return (
    <div>
      <h2>Upload and Summarize Document</h2>
      <input type="file" onChange={onFileChange} />
      <button onClick={onFileUpload}>Upload and Summarize</button>
      {uploadedFileName && <p>Uploaded File: {uploadedFileName}</p>}
      {summary && (
        <div>
          <h3>Summary</h3>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
